def.body:
underline_color_ptr = Pointer(LibUI::UnderlineColor).malloc
LibUI.attribute_underline_color(@ref_ptr, underline_color_ptr, out r, out g, out b, out a)
{underline_color_ptr.value, r, g, b, a}

@for_each_attribute_box = ::Box.box(block)

LibUI.attributed_string_for_each_attribute(@ref_ptr, ->(sender, attr, start, end_, data) do
  callback = ::Box(typeof(block)).unbox(data)

  attribute = Area::Attribute.new(attr)

  callback.call(attribute, start, end_)
end, @for_each_attribute_box.not_nil!)


@for_each_attribute_box = nil

if @released
  return
end
LibUI.free_table_model(@ref_ptr)
@released = true

@model_handler_ref = nil

if @borrowed
  return
end
@@mutex.synchronize do
  @@windows.delete(self)
end
@on_position_changed_box = nil
@on_content_size_changed_box = nil
@on_closing_box = nil
@on_focus_changed_box = nil
super()
